import React, { useState } from 'react';
import Dropdown from "react-bootstrap/Dropdown";
import actions from "../api";

function AddATask(props) {
    const [name, setName] = useState("")
    const [start, setStart] = useState("")
    const [end, setEnd] = useState("")
    const [description, setDescription] = useState("")
    const [status, setStatus] = useState("")
    const [task, setTask] = useState([])
    async function handleSubmit(e) {
        e.preventDefault();

        console.log(status);
        let res = await actions.AddTaskDB({
            name,
            start,
            end,
            description,
            status,
        });
        console.log(res);
    }

    const showTasks = () => {
        return tasks.map((eachTask) => {
          return (
            <Link to={`/tasks/${eachTask._id}`}>
              <li class="eachTaskName">Goal: {eachTask.name}</li>
              <p class="eachTaskDes">Description: {eachTask.description}</p>
            </Link>
          );
        });
      };



    return (
        <div>
            <form onSubmit={handleSubmit} style={{ padding: "80px" }} class="vanillaForm">
                <label for="Name">Task Name</label>
                <input
                    onChange={(e) => setName(e.target.value)}
                    type="text"
                    name="Name"
                />

                <label for="Start Date">Task Start Date </label>
                <input
                    onChange={(e) => setStart(e.target.value)}
                    type="text"
                    name="Start"
                />

                <label for="End Date">Task Due Date</label>
                <input
                    onChange={(e) => setEnd(e.target.value)}
                    type="text"
                    name="End"
                />

                <label for="Description">Description</label>
                <input
                    onChange={(e) => setDescription(e.target.value)}
                    type="text"
                    name="Description"
                />
                <br />
                {/* <label>Set Status</label> */}
                <select name="status" onChange={(e) => setStatus(e.target.value)} >
                    <option>Set Status...</option>
                    <option>Incomplete</option>
                    <option>In Progress</option>
                    <option>Complete</option>
                </select>
                <br />
                <button id="addGoalButton">Add Task</button>
            </form>
        </div>
    );
}

export default AddATask;
