import React, { useState } from "react";
import { Link } from "react-router-dom";
import Dropdown from "react-bootstrap/Dropdown";
import actions from "../api";

function AddATask(props) {
  const [name, setName] = useState("");
  const [start, setStart] = useState("");
  const [end, setEnd] = useState("");
  const [description, setDescription] = useState("");
  const [status, setStatus] = useState("");
  const [open, setOpen] = useState(false);
  console.log(props);
  async function handleSubmit(e) {
    e.preventDefault();

    console.log(status);

    let res = await actions.AddTaskDB({
      name,
      start,
      end,
      description,
      goalId: props.match.params.goalid,
      status,
    });
    console.log(res);
  }
  return (
    <div>
      <ul className="hamburgerMenu" id={open ? "clickedmenu" : ""}>
        {/* <Menu> */}
        <div className="x" onClick={() => setOpen(!open)}>
          X
        </div>
        <Link to="/" style={{ textDecoration: "none" }}>
          <li>Home</li>
        </Link>
        <Link to="/AddAGoal" style={{ textDecoration: "none" }}>
          <li>Add a Goal</li>
        </Link>
        <Link style={{ textDecoration: "none" }}>
          <li>Log In</li>
        </Link>
        <li>About Us</li>
        <li>Profile</li>
        {/* </Menu> */}
      </ul>
      {/* ) : null} */}

      <div className="addAGoalDiv">
        <div className="goalIntro">
          <article id="logobox">
            <img
              src="./images/Copy of Little Leaf Big Tree Logo.png"
              alt="our logo"
              id={open ? "clickedlogo" : ""}
              class="logo2"
              onClick={() => setOpen(!open)}
            />
          </article>
          <article id="introbox">
            <h4 class="goalsintro">Welcome to your Goals page!</h4>
            <span className="goalPageText">
              Each goal you add and complete will take you closer to success!
              <br></br>
              Each goal may be stand-alone, or may include several related
              tasks!{" "}
            </span>
            <br></br>
            <h4 class="goalsintro">Add a New Goal to Your List!</h4>
          </article>
        </div>
        <form
          onSubmit={handleSubmit}
          style={{ padding: "80px" }}
          class="vanillaForm"
        >
          <label for="Name">Task Name</label>
          <input
            onChange={(e) => setName(e.target.value)}
            type="text"
            name="Name"
          />

          <label for="Start Date">Task Start Date </label>
          <input
            onChange={(e) => setStart(e.target.value)}
            type="date"
            name="Start"
          />

          <label for="End Date">Task Due Date</label>
          <input
            onChange={(e) => setEnd(e.target.value)}
            type="date"
            name="End"
          />

          <label for="Description">Description</label>
          <input
            onChange={(e) => setDescription(e.target.value)}
            type="text"
            name="Description"
          />
          <br />
          {/* <label>Set Status</label> */}
          <select name="status" onChange={(e) => setStatus(e.target.value)}>
            <option>Set Status...</option>
            <option>Incomplete</option>
            <option>In Progress</option>
            <option>Complete</option>
          </select>
          <br />
          <button id="addGoalButton">Add Task</button>
        </form>
      </div>
    </div>
  );
}

export default AddATask;
